mapper.select:
SELECT count(*) over() totalCnt ,ROW_NUMBER() OVER () AS rowNm ,BORD_NO AS bordNo ,BORD_NM AS bordNm ,BORD_STUS AS bordStus ,BORD_CNT AS bordCnt ,BORD_RELEASE AS bordRelease ,USER_NM AS userNm ,DATE_FORMAT(MODY_DATE, '%Y-%m-%d') AS modyDate FROM BORDTB WHERE 1=1
AND BORD_NM LIKE CONCAT('%',#{bordNm},'%')
AND USER_NM LIKE CONCAT('%',#{userNm},'%')
AND BORD_NO = #{bordNo}
AND BORD_STUS != 'D' ORDER BY REGI_DATE DESC LIMIT #{start}, ${cntPerPage}

    SELECT USER_PW AS userPw
	 FROM BORDTB
    WHERE BORD_NO = #{bordNum}
